.user-info-panel .level-icon.plus {
  max-height: 24px;
}
.user-info-panel .level-progress-thumb {
  width: 100%;
  height: 100%;
  background: var(--theme-color);
  transform-origin: left;
}
.user-info-panel .level-progress-label {
  font-size: 11px;
}
.user-info-panel .stats {
  display: flex;
  align-items: center;
  align-self: stretch;
  margin: 0 10px;
  line-height: normal;
}
.user-info-panel .stats-item {
  padding: 6px 0;
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: none;
}
.user-info-panel .stats-item:hover {
  color: var(--theme-color) !important;
}
.user-info-panel .stats-item .stats-number {
  font-weight: 600;
  margin-bottom: 4px;
  font-size: 14px;
  transition: none;
}
.user-info-panel .grey-button,
.user-info-panel .theme-button {
  align-self: stretch;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 6px;
  border-radius: 4px;
  font-size: 14px;
}
.user-info-panel .grey-button {
  background: #f8f8f8;
  color: inherit !important;
}
.user-info-panel .grey-button:hover {
  background: #f0f0f0;
}
.user-info-panel .grey-button.logout {
  font-size: 12px;
  height: 32px;
  cursor: pointer;
}
.user-info-panel .grey-button.logout:hover {
  color: inherit !important;
}
body.dark .user-info-panel .grey-button {
  background: #282828;
}
body.dark .user-info-panel .grey-button:hover {
  background: #333;
}
.user-info-panel .theme-button {
  background: var(--theme-color);
  color: var(--foreground-color) !important;
}
.user-info-panel .theme-button:hover {
